mapper.insert:
INSERT INTO order_details(order_num, shop_num, shop_option, shop_qty, shop_price)
VALUES

       (#{order_num}, #{shop_num}, #{shop_option}, #{shop_qty}, #{shop_price});

mapper.delete:
DELETE
FROM cart
WHERE user_idx = (select idx from usersjoin WHERE id = #{user_id})
  AND shop_num = #{shop_num}
  AND shop_option = #{shop_option};
